import React, { Component } from "react";

// Input: liked: boolean
// Output: onClick

class Like extends Component {
  render() {
    let classes = "fa fa-heart";
    if (!this.props.isliked) classes += "-o";
    return (
      <i
        style={{ cursor: "pointer" }}
        onClick={this.props.onClick}
        className={classes}
        aria-hidden="true"
      ></i>
    );
  }
}

export default Like;
